function OpenEmailModal(n,t,i){$("#EmailOptionsHeader").html("Email actions for user "+t);document.getElementById("EmailSendUserId").setAttribute("value",n);document.getElementById("EmailSendUserEmail").setAttribute("value",i);$("#CustomMessageLink").attr("href","/EmailAdmin/CustomEmail/"+n);$("#SendUserEmail").modal("show")}function ResendVerificationCode(){let n=document.getElementById("EmailSendUserId").value;$("#SendUserEmail").modal("hide");$.ajax({url:"/EmailAdmin/ResendEmailVerificationCode",contentType:"application/json",type:"POST",data:JSON.stringify(n),error:function(){alert("Error while trying to send emaile message.")},complete:function(n){DisplayMessages(n)}})}function SendPasswordResetRequest(){let n=document.getElementById("EmailSendUserEmail").value;CreatePasswordResetRequest(n,ValidateRequestResponse)}function ValidateRequestResponse(n){let t=JSON.parse(n.responseText);if(t.isFaulted)$("#SendUserEmail").modal("hide"),DisplayMessages(n);else{let n=document.getElementById("EmailSendUserEmail").value;SendPasswordResetMessage(n,DisplayResetPasswordResult)}}function DisplayResetPasswordResult(n){$("#SendUserEmail").modal("hide");DisplayMessages(n)}