function ResetFilters(){$("#CategoryFilter").val("");$("#ManufacturerFilter").val("");$("#ModelNameFilter").val("")}function EraseCubesTableContent(){$("#CubesTable").empty()}function GetData(n){let t=$("#CategoryFilter").val(),i=$("#ManufacturerFilter").val(),r=$("#ModelNameFilter").val();$.get("/Home/GetCubesData/",{category:t,manufacturer:i,modelName:r,page:n},function(n){ResponseCheck(n,HandleCubesResponse)})}function HandleCubesResponse(n){amountOfPages=n.pagination.amountOfPages;currentPage=n.pagination.currentPage;pageSize=n.pagination.pageSize;pageRecordsAmount=n.cubes.length;PopulateCubesTable(n.cubes)}function PopulateCubesTable(n){if(EraseCubesTableContent(),n.length<1)AddNoResultsRow();else for(var t=0;t<n.length;t++)AddSingleCubeRecord(n[t],t+1);CreatePagination()}function AddNoResultsRow(){$("#CubesTable").append('<tr><td class="align-middle text-center" colspan="8">No data to display<\/td><\/tr>')}function AddSingleCubeRecord(n,t){$("#CubesTable").append('<tr><td class="align-middle">'+GetRecordNumber(t)+'<\/td><td class="align-middle">'+n.category.name+'<\/td><td class="align-middle">'+n.manufacturer.name+'<\/td><td class="align-middle">'+n.modelName+'<\/td><td class="align-middle">'+n.plasticColor.name+'<\/td><td class="align-middle">'+n.rating+'<\/td><td class="align-middle">'+n.releaseYear+'<\/td><td class="align-middle">'+BoolToYN(n.wcaPermission)+"<\/td><\/tr>")}function GetRecordNumber(n){return(currentPage-1)*pageSize+n}function BoolToYN(n){return n==!0?"Yes":"No"}function ChangePage(n){GetData(n)}$(document).ready(function(){GetData(1)});$("#OpenFiltersButton").click(function(){$("#CubesFilterModal").modal("show")});$("#UpdateFiltersButton").click(function(){GetData(1);$("#CubesFilterModal").modal("hide")});$("#ResetFiltersButton").click(function(){ResetFilters();GetData(1);$("#CubesFilterModal").modal("hide")});