function InitializeCategoriesChart(n){window.categoryChart=new Chart(document.getElementById("CategoriesPie").getContext("2d"),{type:"pie",data:categoryChartData,options:{responsive:!0,tooltips:n,legend:{display:!1}}})}function SendInitialrequest(){$.get("/Home/GetIndexChartData",function(n){if(n.isFaulted){let t={};t.responseText=JSON.stringify(n);DisplayMessages(t)}else PopulateCategoryPie(n.jsonData)})}function PopulateCategoryPie(n){if(n.categoryChartData.length>0)for(var t=0;t<n.categoryChartData.length;t++)AddDataToCategoryPie(n.categoryChartData[t].amount,n.categoryChartData[t].category.name);window.categoryChart.update()}function AddDataToCategoryPie(n,t){categoryChartData.datasets[0].data.push(n);categoryChartData.datasets[0].backgroundColor.push(chartColors[GetColorIndex(categoryChartData.datasets[0].data)]);categoryChartData.labels.push(t)}let categoryChartData={datasets:[{data:[],backgroundColor:[],label:"Most favourite categories"}],labels:[]};$(document).ready(function(){let n={enabled:!0,displayColors:!0,titleFontSize:14,bodyFontSize:14,titleAlign:"center",bodyAlign:"center",bodySpacing:5,callbacks:{label:function(n,t){return t.labels[n.index]," "+t.labels[n.index]+": "+t.datasets[0].data[n.index]+" series"}}};InitializeCategoriesChart(n);SendInitialrequest()});