function AddScramble(n){document.location="/Categories/AddScramble/"+n}function ChangeCategory(n){document.location="/Categories/Scramble/"+n}function ModifyDefinition(n){document.location="/Categories/ModifyScramble/"+n}function SetAsDefault(n,t){$.post("/Categories/SetScrambleAsDefault",{categoryId:n,scrambleId:t},function(n){ResponseCheck(n,HandleSetDefaultData)})}function HandleSetDefaultData(n){n===!0&&document.location.reload()}function ConfirmScrambleDeletion(n,t,i,r){$("#DeleteScrambleId").val(n);$("#ScrName").text(t);$("#CatName").text(r);$("#DeleteScrambleModal").modal("show")}function EditMoves(n){$.get("/Categories/GetScrambleDefinition/",{scrambleId:n},function(n){ResponseCheck(n,ShowMovesModal)})}function ShowMovesModal(n){$("#ModifiedMovesScrambleId").val(n.scramble.identity);$("#MoveModificationErrorMessage").text("");$("#ScrNameM").text(n.scramble.name);$("#CatNameM").text(n.scramble.category.name);$("#MovesList").val(GetMovesString(n.moves));$("#MovesModificationModal").modal("show")}function GetMovesString(n){if(n.length<1)return"";let i="";for(var t=0;t<n.length;t++)i+=n[t].move,t<n.length-1&&(i+=" ");return i}function HandleMovesResponse(n){n==""?($("#MovesModificationModal").modal("hide"),document.location.reload()):$("#MoveModificationErrorMessage").text(n)}$("#AddScrambleButton").click(function(){AddScramble($("#CategoryId").val())});$("#ChangeCategoryButton").click(function(){$("#AllCategoriesModal").modal("show")});$("#CategoriesWithNoScrambleButton").click(function(){$("#CategoriesNoScrambleModal").modal("show")});$("#ScrambleDeletionConfirmedButton").click(function(){let n=$("#DeleteScrambleId").val();$.post("/Categories/DeleteScrambleDefinition",{scrambleId:n},function(n){ResponseCheck(n,HandleSetDefaultData)})});$("#MovesModificationConfirmButton").click(function(){let n=$("#MovesList").val(),t=$("#ModifiedMovesScrambleId").val();$.post("/Categories/ModifyMoves/",{scrambleId:t,moves:n},function(n){ResponseCheck(n,HandleMovesResponse)})});