function CheckPasswords(){let n=$("#Password").val(),i=$("#Password2").val(),t=n===i&&n.length>=8;return t?document.getElementById("PasswordNotEqual").classList.add("d-none"):document.getElementById("PasswordNotEqual").classList.remove("d-none"),t}function CheckEmail(){let n=document.getElementById("Email").value,t=n.includes("@")&&n.includes(".");return t?document.getElementById("BadEmail").classList.add("d-none"):document.getElementById("BadEmail").classList.remove("d-none"),t}function CheckUserName(){let t=document.getElementById("UserName").value,n=t.length>=8;return n?document.getElementById("BadUserName").classList.add("d-none"):document.getElementById("BadUserName").classList.remove("d-none"),n}function Validate(n){let t=CheckPasswords(),i=CheckEmail(),r=CheckUserName();t&&i&&r||n.preventDefault(n)}$("#Password").blur(function(){CheckPasswords()});$("#Password2").blur(function(){CheckPasswords()});$("#Email").blur(function(){CheckEmail()});$("#UserName").blur(function(){CheckUserName()});$("#CreateForm").submit(function(n){Validate(n)});